import React, { useState, useEffect } from 'react';
import { AiFillDelete, CiCirclePlus } from "react-icons/ai";
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
    const [users, setUsers] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await fetch('http://localhost:3000/prospects');
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const data = await response.json();
                setUsers(data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchUsers();
    }, []);

    const handleAction = () => {
        navigate('/studentform');
    };

    const handleDelete = (userId) => {
        // Your handleDelete logic goes here
    };

    // Function to group users by subject area
    const groupUsersBySubjectArea = () => {
        const groupedUsers = {};
        users.forEach(user => {
            const subjectArea = user.subject_area;
            if (!groupedUsers[subjectArea]) {
                groupedUsers[subjectArea] = [];
            }
            groupedUsers[subjectArea].push(user);
        });
        return groupedUsers;
    };

    return (
        <div>
            <div className="title">
                <h2 className="text-gray-700 text-3xl mt-5 font-bold">List of Submissions</h2>
            </div>

            <div className='flex flex-row justify-center mr-5'>
                <button onClick={handleAction} className="text-red-500 flex items-center">
                    <span className='text-red text-xl font-bold'>Add a form</span>
                    <CiCirclePlus className="w-5 h-5 ml-1" />
                </button>
            </div>

            <div className="user-list">
                {Object.entries(groupUsersBySubjectArea()).map(([subjectArea, usersWithSubjectArea]) => (
                    <div key={subjectArea}>
                        <div className='border border-1 border-gray-300 rounded bg-gray-100 px-3 py-2 m-5 shadow shadow-md' style={{ width: "50em" }}>
                            <div><strong>Subject Area:</strong> {subjectArea}</div>
                            {usersWithSubjectArea.map(user => (
                                <div key={user.id} className="border border-1 border-gray-300 rounded bg-gray-100 px-3 py-2 my-2 shadow shadow-md" style={{ width: "48em", marginLeft: "2em" }}>
                                    <div><strong>Full Name:</strong> {user.full_name}</div>
                                    <div><strong>Email:</strong> {user.email}</div>
                                    <div><strong>Date of Birth:</strong> {user.date_of_birth}</div>
                                    <div><strong>Location:</strong> {user.gps_location}</div>
                                    <div><strong>Agreed Marketing:</strong> {user.marketing_updates ? "Yes" : "No"}</div>
                                    <div><strong>Agreed Correspondence:</strong> {user.correspondence_in_welsh ? "Yes" : "No"}</div>
                                    {/* Add other user details here */}
                                    <button onClick={() => handleDelete(user.id)} className="text-red-500">
                                        <AiFillDelete className="w-8 h-8" />
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Dashboard;
